mapper.insert:
insert into board_tb
values(
#{board_seq}, #{board_division}, #{board_title}, #{board_content}, #{board_hit}, #{board_use_yn}, #{board_writer}, now(), #{board_updateid}, now(), #{board_etc}
);
SELECT LAST_INSERT_ID();

mapper.update:
update board_tb
set board_title = #{board_title}, board_content = #{board_content}, board_updateid = #{board_updateid}, board_update_date = now()
where board_seq = #{board_seq}
